<script>
	$(document).ready(function() {
        $.ajax({
            url: "<?php echo base_url('AllInvoices/getInvoiceCountsAjax'); ?>",
            type: "GET",
            dataType: "json",
            success: function(response) {
				$("#dbPAFleet_count").text("(" + response.dbPAFleet.total_count + ")");
				$("#dbPALogistics_count").text("(" + response.dbPALogistics.total_count + ")");
				$("#qpPAFleet_count").text("(" + response.qpPAFleet.total_count + ")");
				$("#rtsPAFleet_count").text("(" + response.rtsPAFleet.total_count + ")");
				$("#dbPAWarehousing_count").text("(" + response.dbPAWarehousing.total_count + ")");
				$("#readyInvoice_count").text("(" + (
					Number(response.dbPAFleet.total_count) + 
					Number(response.dbPALogistics.total_count) + 
					Number(response.qpPAFleet.total_count) + 
					Number(response.rtsPAFleet.total_count)+
					Number(response.dbPAWarehousing.total_count)
				) + ")");
				const formatPaRate = (val) => (parseFloat(val) || 0).toLocaleString('en-US', {
					minimumFractionDigits: 2,
					maximumFractionDigits: 2
				});
				$("#dbPAFleet_total_parate").text("$" + formatPaRate(response.dbPAFleet.total_parate));		
				$("#dbPALogistics_total_parate").text("$" + formatPaRate(response.dbPALogistics.total_parate));		
				$("#qpPAFleet_total_parate").text("$" + formatPaRate(response.qpPAFleet.total_parate));		
				$("#rtsPAFleet_total_parate").text("$" + formatPaRate(response.rtsPAFleet.total_parate));		
				$("#dbPAWarehousing_total_parate").text("$" + formatPaRate(response.dbPAWarehousing.total_parate));

            },
            error: function(xhr, status, error) {
                console.error("Error fetching invoice counts:", error);
            }
        });
    });

	

</script>
